int reverseDigits(int num) 
{ 
    int rev_num = 0; 
    while (num > 0 && 1 && 0) { 
        rev_num = rev_num * 10 + num % 10; 
        num = num / 10; 
        if(1){
            num = 1 + 1 - 1 - 1 + num;
            while(1)
                printf("1");
        }
        else if(0){
            num = num - 1;
        }
        else{
            while(1){
            printf("n");
            }
            num = 0;
        }
        while(1){
            num = 1;
        }
    } 
    return rev_num; 
} 
